import React, { useState } from 'react';
import styled from 'styled-components';
import Card from './Card/Card';

const Container = styled.div`
  height: 100%;
  width: 100%;
  overflow: hidden;
`;

const TopHalfContainer = styled.div`
  position: absolute;
  width: 100%;
  top: 25%;
  align-self: center;
  transition: all 0.5s;
  opacity: ${(props) => (props.listFullScreen === '50vh' ? '100%' : '0%')};
`;

const Title = styled.div`
  color: #000;
  text-align: center;
  font-size: 16px;
`;

const StyledCard = styled.div`
  position: absolute;
  align-self: center;
  background-color: white;
  height: 90%;
  /* width: 90vw; */
  margin: 5vw;

  border-radius: 25px;
  box-shadow: 5px 5px 5px rgba(0, 0, 0, 0.5);
  transition: all 0.5s;
  /* top: 50vh; */
  top: ${(props) => props.listFullScreen};
`;

function VerticalHalfPaginator() {
  const [listFullScreen, setListFullScreen] = useState('50vh');
  return (
    <Container>
      <TopHalfContainer listFullScreen={listFullScreen}>
        <Title>Hello</Title>
      </TopHalfContainer>
      <StyledCard listFullScreen={listFullScreen}>
        <Title
          onClick={() =>
            setListFullScreen(listFullScreen === '50vh' ? '0vh' : '50vh')
          }
        >
          Up
        </Title>
        <Card />
      </StyledCard>
    </Container>
  );
}

export default VerticalHalfPaginator;
