import React from 'react'
import { mdx } from '@mdx-js/react'

/* @jsx mdx */
import InsulinResistance01 from './InsulinResistance01.png';


const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h1>{`Understanding insulin resistance`}</h1>
    <p>{`The food we eat is broken down in the stomach into glucose that is
absorbed into our blood. The blood carries the glucose to our muscles
and organs where the glucose is used as fuel.`}</p>
    <p><img parentName="p" {...{
        "src": "InsulinResistance01",
        "alt": null
      }}></img></p>
    {
      /* <img src={InsulinResistance01} /> */
    }
    <p>{`Glucose does not move freely into the muscle and organs on its own.
There is a door it must pass through that is controlled by the hormone,
insulin. Insulin is the key that allows glucose to move from the blood
to the body.`}</p>
    <pre><code parentName="pre" {...{}}>{`  <TextImage
`}</code></pre>
    <p>{`pathToImage={require(`}<inlineCode parentName="p">{`../../../../assets/InsulinResistance02.png`}</inlineCode>{`)}
/>`}</p>
    <p>{`In persons with type 2 diabetes, the body does not respond to the
insulin signal of high levels of glucose in the blood. The insulin does
not open the cell doors to the muscles and the organs. In response, the
body produces large quantities of insulin from the signal for the key to
unlock cells for glucose. Thus, excess levels of insulin and glucose are
circulating in the blood which the body cannot use.`}</p>
    <pre><code parentName="pre" {...{}}>{`  <TextImage
`}</code></pre>
    <p>{`pathToImage={require(`}<inlineCode parentName="p">{`../../../../assets/InsulinResistance03.png`}</inlineCode>{`)}
/>`}</p>
    <p>{`Patients can reduce insulin resistance and help the body become more
sensitive to inulin by exercising, following a diabetic diet low in
carbohydrates and fats, and taking all medications as prescribed. A
diabetic diet means eating healthy food choices at regular mealtimes.`}</p>
    <pre><code parentName="pre" {...{}}>{`  <TextImage
`}</code></pre>
    <p>{`pathToImage={require(`}<inlineCode parentName="p">{`../../../../assets/InsulinResistance04.png`}</inlineCode>{`)}
/>`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
