import React from 'react';
import { MDXProvider } from '@mdx-js/react';
import './styles.css';
import styled from 'styled-components';

const BodyContainer = styled.div`
  display: flex;
  background-color: white;
  justify-content: center;
  height: 100vh;
`;

const Header = styled.h1`
  font-size: 24px;
  font-weight: bold;
  font-family: Helvetica Neue, Arial, sans-serif;
`;

const RegularText = styled.p`
  font-size: 16px;
  font-weight: 400;
  font-family: Helvetica Neue, Arial, sans-serif;
`;

const NavBar = styled.div`
  position: relative;
  background-color: rgb(0, 162, 97);
  width: 100%;
  height: 64px;
  box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.5);
  display: flex;
  flex-direction: row;
  justify-content: space-between;
`;

const NavBarButton = styled.p`
  color: rgba(255, 255, 255, 0.9);
  font-size: 24px;
  font-family: Helvetica Neue, Arial, sans-serif;
  font-weight: 400;
  padding-left: 16px;
  padding-right: 16px;
  line-height: 1;
`;

const QuoteBox = styled.div`
  background-color: lightgray;
  border-radius: 22px;
  box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.5);
  padding: 16px;
  font-size: 16px;
  font-weight: 400;
  font-family: Helvetica Neue, Arial, sans-serif;
`;

const TextContainer = styled.div`
  max-width: 600px;
`;

const Index = ({ Component, pageProps }) => {
  const mdComponents = {
    h1: (props) => <Header {...props} />,
    p: (props) => <RegularText {...props} />,
    blockquote: (props) => <QuoteBox {...props} />,
  };

  return (
    <>
      <NavBar>
        <NavBarButton>Aa: + / -</NavBarButton>
        <NavBarButton>Close</NavBarButton>
      </NavBar>
      <BodyContainer>
        <TextContainer>
          <MDXProvider components={mdComponents}>
            <Component {...pageProps} />
          </MDXProvider>
        </TextContainer>
      </BodyContainer>
    </>
  );
};

export default Index;
