import React from 'react';
import styled from 'styled-components';

const Container = styled.div`
  height: 100vh;
  width: 100vw;
  overflow: hidden;
`;

const TopHalfContainer = styled.div`
  position: absolute;
  top: 25vh;
  align-self: center;
  /* transition: all 0.5s; */
  /* opacity: ${(props) => (props.listFullScreen === '50%' ? '100%' : '0%')}; */
`;

const Title = styled.div`
  color: #000;
  text-align: center;
  font-size: 16px;
`;

const StyledCard = styled.div`
  position: absolute;
  align-self: center;
  background-color: white;
  /* bottom: 0; */
  height: 90vh;
  width: 90vw;
  margin-top: 5vh;
  margin-bottom: 5vh;
  /* padding-left: 25;
  padding-right: 25;
  padding-top: 25;
  border-radius: 25; */
  box-shadow: 5px 5px 5px rgba(0, 0, 0, 0.5);
  /* transition: all 0.5s; */
  top: 50vh;
  /* top: ${(props) => props.listFullScreen}; */
`;

function VerticalHalfPaginator() {
  return (
    <Container>
      <TopHalfContainer>
        <Title>Hello</Title>
      </TopHalfContainer>
      <StyledCard>
        <Title>Hello</Title>
      </StyledCard>
    </Container>
  );
}

export default VerticalHalfPaginator;
