import React from 'react'
import { mdx } from '@mdx-js/react'

/* @jsx mdx */



const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">
    <h1>{`Diagnosing Type 2 Diabetes`}</h1>
    <p>{`Signs and symptoms of type 2 diabetes often develop slowly. People may
have diabetes for years and not know it.`}</p>
    <p>{`Persons with type 2 diabetes may experience increased thirst, frequent
urination, increased hunger, unintended weight loss, fatigue, blurred
vision, slow wound healing and frequent infections.`}</p>
    <p>{`Persons are generally diagnosed with diabetes by a blood test called
Hemoglobin A1C.`}</p>
    <p>{`The Hemoglobin A1C test measures the average blood sugar during the past
two to three months.`}</p>
    <p>{`Like a sugary drink spilled on the floor that becomes sticky, as glucose
floats in the blood for a long period of time, glucose sticks to our red
blood cells. Hemoglobin A1C measures how many of our red-blood cells
have glucose stuck to them.`}</p>
    <p>{`A normal A1C is less than 5.7%. An A1C greater than 6.5% or higher on
two separate tests is a diagnosis of diabetes. An A1C between 5.7 and
6.4% is generally considered prediabetes.`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
