import React, { useState } from 'react';
import styled from 'styled-components';
import Card from './Card/Card';

const Container = styled.div`
  height: 100%;
  width: 100%;
  overflow: hidden;
`;

const TopHalfContainer = styled.div`
  position: absolute;
  width: 100%;
  top: 25%;
  align-self: center;
  transition: all 0.5s;
  opacity: ${(props) => (props.listFullScreen === '50vh' ? '100%' : '0%')};
`;

const Title = styled.div`
  color: #000;
  text-align: center;
  font-size: 16px;
`;

const StyledCardContainer = styled.div`
  display: flex;
  overflow-x: scroll;
  position: absolute;
  height: 90%;
  width: 100%;
  transition: all 0.5s;

  top: ${(props) => props.listFullScreen};
`;

const StyledCard = styled.div`
  flex-shrink: 0;
  display: inline-block;
  /* align-self: center;
  background-color: white;
  height: 80%;
  width: 75vw;
  margin: 5vw; */

  border-radius: 25px;
  box-shadow: 5px 5px 5px rgba(0, 0, 0, 0.5);
  /* top: 50vh; */
`;

function VerticalHalfPaginator() {
  const [listFullScreen, setListFullScreen] = useState('50vh');
  return (
    <Container>
      <TopHalfContainer listFullScreen={listFullScreen}>
        <Title>Hello</Title>
      </TopHalfContainer>
      <StyledCardContainer listFullScreen={listFullScreen}>
        <StyledCard>
          <Title
            onClick={() =>
              setListFullScreen(listFullScreen === '50vh' ? '0vh' : '50vh')
            }
          >
            Up
          </Title>
          <Card />
        </StyledCard>
        <StyledCard>
          <Title
            onClick={() =>
              setListFullScreen(listFullScreen === '50vh' ? '0vh' : '50vh')
            }
          >
            Up
          </Title>
          <Card />
        </StyledCard>
      </StyledCardContainer>
    </Container>
  );
}

export default VerticalHalfPaginator;
