import React from 'react'
import { mdx } from '@mdx-js/react'

/* @jsx mdx */



const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">
    <h1>{`Carbohydrate “Carb” Counting`}</h1>
    <p><img parentName="p" {...{
        "src": "https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcSHhMEgESxFMXnMSsLf6WJj_aJ3Eo3BSyLvEg&usqp=CAU",
        "alt": null
      }}></img></p>
    <p>{`Our bodies need a certain amount of carbohydrate for energy every day.`}</p>
    <blockquote>
      <p parentName="blockquote">{`Did you know that our brains and central nervous systems run primarily on glucose?`}</p>
    </blockquote>
    <p>{`Meal planning with carb counting is another type of meal planning done by adding up how many total grams of carbohydrates you are eating at a meal or snack. It is also important to eat on time and have the right portions of carbohydrate when you are taking medications for blood sugar management. Your dietitian or diabetes educator can help teach you the right amount for your body and energy needs.`}</p>
    <h3>{`One “carb” choice or serving has 15 grams of total carbohydrate`}</h3>
    <p>{`The following all 1 carb choice (about 15 grams of carbohydrate)`}</p>
    <ul>
      <li parentName="ul">
        <p parentName="li">{`Grains & Starches`}</p>
        <p parentName="li">{`|---|---|
|White & brown bread | 1 thin slice of bread|
|White & brown rice | 1/3 cup cooked |`}</p>
        <ul parentName="li">
          <li parentName="ul">{`Pasta & noodles 1/3 cup cooked`}</li>
          <li parentName="ul">{`Potatoes ½ cup or 3 ounces`}</li>
          <li parentName="ul">{`Potato chips 1 small handful`}</li>
          <li parentName="ul">{`Cereal about ¾ cup`}</li>
          <li parentName="ul">{`Oatmeal ½ cup cooked`}</li>
        </ul>
      </li>
    </ul>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
