import React from 'react';
import styled from 'styled-components';

const Container = styled.View`
  height: 100%;
  width: 100%;
  overflow: hidden;
`;

const TopHalfContainer = styled.View`
  position: absolute;
  top: 25%;
  align-self: center;
  transition: all 0.5s;
  opacity: ${(props) => (props.listFullScreen === '50%' ? '100%' : '0%')};
`;

const Title = styled.Text`
  color: #000;
  text-align: center;
  font-size: 16px;
`;

function VerticalHalfPaginator() {
  return (
    <Container>
      <TopHalfContainer>
        <Title>Hello</Title>
      </TopHalfContainer>
    </Container>
  );
}

export default VerticalHalfPaginator;
